import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonGrid, IonRow, IonCol, IonIcon, IonButtons, IonButton, IonItem, IonInput } from '@ionic/react';
import React from 'react';
import ExploreContainer from '../components/ExploreContainer';
import '../css/ForgetPassword2.css';
import '../css/Global.css';
import { arrowBackOutline } from 'ionicons/icons';
import verificationCode from '../img/forgetPassword/verificationCode.png';
import { useForm, Controller } from "react-hook-form";

const ForgetPassword2: React.FC = () => {
    const { register, handleSubmit, errors } = useForm();

    const onSubmit = (data: any) => {
        console.log("Submitted");
        console.log(data);
    };

    return (
      <IonPage>
        <IonHeader>
          <IonToolbar id="topBar">
            <IonButtons slot="start">
                <IonButton>
                    <IonIcon id="back_button" slot="icon-only" icon={arrowBackOutline} />
                </IonButton>
            </IonButtons>

            <IonTitle id="title">
                Forget Password?  
            </IonTitle>
          </IonToolbar>
        </IonHeader>

        {/* Screen Content*/}
        <IonContent fullscreen style={{display: "flex"}}>
            <IonGrid>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <IonRow class="ion-justify-content-center ion-align-items-center">
                        <IonCol></IonCol>
                        <IonCol size="6">
                            <img id="vcImg" src={verificationCode}></img>
                        </IonCol>
                        <IonCol></IonCol>
                    </IonRow>

                    <IonRow>
                        <IonCol></IonCol>
                        <IonCol size="10">
                            <p id="text1"><b>Please enter the verification code.</b></p>
                            <p id="text2">The verification code has been sent to your registered email ID.</p>
                            <IonItem>
                                <IonInput id="codeID" type="number" placeholder="Verification Code" name="codeID" maxlength={6}                                 
                                ref={register({
                                    required: "Verification code is required!",
                                    pattern: {
                                        value: /^-?\d+\.?\d*$/,
                                        message: "Please enter a valid verification code!"
                                    }
                                  })}>
                                </IonInput>
                            </IonItem>
                            <p id="errorMsg">
                                <span>
                                    {errors.codeID && errors.codeID.message}
                                </span>
                            </p>
                        </IonCol>
                        <IonCol></IonCol>
                    </IonRow>

                    <IonRow class="ion-justify-content-center" style={{marginTop:"10%"}}>
                        <IonButton size="large" id="nextBtn" type="submit">NEXT</IonButton>
                    </IonRow>
                </form>
            </IonGrid>
        </IonContent>
      </IonPage>
    );
  };
  
  export default ForgetPassword2;
