import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonGrid, IonRow, IonCol, IonIcon, IonButtons, IonButton, IonItem, IonInput } from '@ionic/react';
import React, {useRef, useState} from 'react';
import '../css/ForgetPassword2.css';
import '../css/Global.css';
import '../css/Test.css';
import { arrowBackOutline, enter, keypadOutline } from 'ionicons/icons';
import { useForm, Controller } from "react-hook-form";
import VerificationInput from 'react-verification-input';
import TestController from '../components/TestController';


const Test: React.FC = () => {
    
    const { register, handleSubmit, errors } = useForm();

    const onSubmit = (data: any) => {
        console.log("Submitted");
        console.log(data);
    };

    const [enteredCode, setEnteredCode] = useState<string>();

    const codeInputRef = useRef<HTMLIonInputElement>(null);
    
    const verifyCode = () => {
        const enteredCode = codeInputRef.current!.value!;
        console.log(enteredCode);
        const code = String(enteredCode);
        setEnteredCode(code);
    };

    return (
      <IonPage>
        <IonHeader>
          <IonToolbar id="topBar">
            <IonButtons slot="start">
                <IonButton>
                    <IonIcon id="back_button" slot="icon-only" icon={arrowBackOutline} />
                </IonButton>
            </IonButtons>

            <IonTitle id="title">
                Forget Password?  
            </IonTitle>
          </IonToolbar>
        </IonHeader>

        {/* Screen Content*/}
        <IonContent fullscreen style={{display: "flex"}}>
            <IonGrid>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <IonRow class="ion-justify-content-center">
                        <IonCol></IonCol>
                        <IonCol size="6" style={{textAlign: "center"}}>
                            <IonIcon id="key_logo" icon={keypadOutline} />
                        </IonCol>
                        <IonCol></IonCol>
                    </IonRow>

                    <IonRow>
                        <IonCol></IonCol>
                        <IonCol size="10">
                            <p id="text1"><b>Please enter the verification code.</b></p>
                            <p id="text2">The verification code has been sent to your registered email ID.</p>
                            <IonItem>
                                <IonInput id="codeID" type="text" placeholder="Verification Code" name="codeID" maxlength={6}                          
                                ref= {codeInputRef}>
                                </IonInput>
                            </IonItem>
                            <p id="errorMsg">
                                <span>
                                    {errors.codeID && errors.codeID.message}
                                </span>
                            </p>
                        </IonCol>
                        <IonCol></IonCol>
                    </IonRow>
                    <TestController onVerify={verifyCode}/>                 

                    <IonRow>
                        <IonCol id="test">
                            <VerificationInput/>
                        </IonCol>
                    </IonRow>
                
                </form>
            </IonGrid>
        </IonContent>
      </IonPage>
    );
  }

export default Test;
