import { IonButton, IonCol, IonFooter, IonGrid, IonIcon, IonItem, IonItemOption, IonItemOptions, IonItemSliding, IonRow, IonTitle, IonToolbar } from '@ionic/react';
import React, { useRef, useState } from 'react';

import '../../css/Global.css';
import '../../css/CourseFilterPopoverContent.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { addCircle, removeCircle } from 'ionicons/icons';

const CourseFilterPopoverContent: React.FC<{
    removeProg: any;
    removeAllProg: any;
    viewResults: any;
    params: any;
    href: any;
}> = props => {

    const info = useRef<HTMLIonRowElement>(null);
    const showIcon = useRef<HTMLIonIconElement>(null);

    const displayInfoCol = () => {
        info.current!.hidden = !info.current!.hidden;
        if (showIcon.current!.icon == addCircle)
            showIcon.current!.icon = removeCircle;
        else
            showIcon.current!.icon = addCircle;
    };

    return (
        <>
            <IonGrid id="courseFilterPopoverGrid">
                <IonRow className="filterCourseHeaderRow">
                    <IonCol className="filterCourseHeaderCol">

                        <IonRow className="filterCourseInnerRow" class="ion-align-items-center">
                            <IonCol size="10" sizeSm="10" style={{ padding: "0" }} class="ion-text-left">
                                <div className="ion-text-wrap">
                                    <IonTitle className="filterCourseHeader">
                                    
                                        Mode of Study
                                    
                                </IonTitle>
                                </div>
                            </IonCol>

                            <IonCol size="2" sizeSm="2" className="toggleFilterCourseBtnCol">
                                <IonButton className="toggleFilterCourseBtn" onClick={displayInfoCol} fill="clear" size="default">
                                    <IonIcon slot="icon-only" ref={showIcon} icon={addCircle} />
                                </IonButton>
                            </IonCol>
                        </IonRow>

                        <IonRow class="ion-align-items-center">
                            <IonCol sizeSm="12" className="courseFilters" ref={info} hidden={true}>
                                <div className="ion-text-wrap">
                                    <p>Art</p>
                                </div>
                            </IonCol>
                        </IonRow>
                    </IonCol>
                </IonRow>  

            </IonGrid>
        </>
    );
};

export default CourseFilterPopoverContent;
