import { IonButton, IonButtons, IonCol, IonContent, IonGrid, IonHeader, IonIcon, IonImg, IonItem, IonItemDivider, IonLabel, IonList, IonMenu, IonMenuButton, IonPage, IonRouterLink, IonRow, IonSegment, IonSegmentButton, IonSplitPane, IonText, IonTitle, IonToolbar } from '@ionic/react';
import React, { useState } from 'react';

import "../css/Global.css";
import "../css/MySchedule.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faReadme } from '@fortawesome/free-brands-svg-icons';
import { faCalendar, faCalendarAlt, faComments, faBell, faUserCircle } from '@fortawesome/free-regular-svg-icons';
import { faHome, faMapSigns, faMapMarkedAlt, faInfoCircle, faPhotoVideo, faBookOpen, faHands, faCog, faQrcode, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

import TopNav from '../components/TopNav';
import Menu from '../components/Menu';
import MyScheduleContent from '../components/MyScheduleContent';

const MySchedule: React.FC = () => {
    const [dayNum, setDayNum] = useState("day1");

    const handleDayOne = () => {
        setDayNum("day1");
      };
    
      const handleDayTwo = () => {
        setDayNum("day2");
      };

    return (
        <IonPage id="main">
            <TopNav title="My Schedule" route='/u/home' backarrow={ true } hamburger = { true }/>
            
            <IonContent fullscreen={true} className="myScheduleIonContent">
                <IonGrid className="myScheduleGrid">
                    <IonRow className="ion-justify-content-center openHouseDateRow">
                        <IonCol size-sizeSm="6" className="openHouseDateCol">
                            <span className="openHouseDateTitleText">From: </span> 
                            <span className="openHouseDateText">21 November 2020</span>
                        </IonCol>
                        <IonCol size-sizeSm="6" className="openHouseDateCol">
                            <span className="openHouseDateTitleText">To: </span> 
                            <span className="openHouseDateText">22 November 2020</span>
                        </IonCol>
                    </IonRow>

                    <IonRow className="mySchedule-IonRowCol">
                        <IonToolbar>
                            <IonSegment scrollable value={dayNum} onIonChange={(e) => console.log(`${e.detail.value}`)}>
                                <IonSegmentButton value="day1" onClick={() => handleDayOne()} className="mySchedule-DayTab">
                                    Day 1: 21 Nov 2020
                                </IonSegmentButton>
                                
                                <IonSegmentButton value="day2" onClick={() => handleDayTwo()} className="mySchedule-DayTab">
                                    Day 2: 22 Nov 2020
                                </IonSegmentButton>
                            </IonSegment>
                        </IonToolbar>
                    </IonRow>
                </IonGrid>

                <MyScheduleContent day1={dayNum} day2={dayNum} />
            </IonContent>
        </IonPage>
    );
};

export default MySchedule;
