import { IonCol, IonContent, IonGrid, IonImg, IonPage, IonRow, IonSegment, IonSegmentButton, IonSlide, IonSlides, IonToolbar } from '@ionic/react';
import React from 'react';

import '../css/Global.css';
import '../css/CampusFacilitiesMap.css';

import blkA from '../img/campusFacilities/blkA.png';

import TopNav from '../components/TopNav';
import Menu from '../components/Menu';

const CampusFacilitiesMap: React.FC = () => {

    const slideOpts = {
        initialSlide: 0,
        speed: 400,
    };

    return (
        <IonPage>
            <TopNav title="Campus Facilities Map" route='/u/home' backarrow={ true } hamburger = { true }/>
            
            <IonContent fullscreen={true} className="campusFacilMapIonContent">
                {/* Embeded Campus Map */}
                {/*<IonGrid className="campusFacilGrid">
                    <IonRow id="campusMapImg">
                        <div style={{width: "100%"}}>
                            <iframe width="100%" height="500" frameBorder="0" scrolling="no" marginHeight={0} marginWidth={0} src="https://maps.google.com/maps?width=100%25&amp;height=500&amp;hl=en&amp;q=461%20Clementi%20Rd,%20Singapore%20599491+(Singapore%20Institute%20of%20Management)&amp;t=k&amp;z=18&amp;ie=UTF8&amp;iwloc=B&amp;output=embed"></iframe>
                        </div>
                    </IonRow>
                </IonGrid> */}


                <IonSlides pager={true} options={slideOpts} id="campusFacilSlides">
                    <IonSlide className="slide">
                        <IonGrid className="slideGridRowCol">
                            <IonRow className="slideGridRowCol">
                                <IonCol size-sizeSm="12" className="imgCol slideGridRowCol">
                                    <IonImg src={ blkA }></IonImg>
                                </IonCol>
                            </IonRow>

                            <IonRow className="slideGridRowCol">
                                <IonCol size-sizeSm="12" className="slideGridRowCol">
                                    <h4 className="blkTitle">Block A</h4>
                                </IonCol>
                            </IonRow>

                            <IonRow className="campusFacilTableRow">
                                <IonCol size-sizeSm="6" className="campusFacilTableHeader" style={{borderRight: "1px solid #dddddd"}}>
                                    Facility
                                </IonCol>

                                <IonCol size-sizeSm="6" className="campusFacilTableHeader">
                                    Location
                                </IonCol>
                            </IonRow>

                            {/* Rows below to be generated dynamically along with the data*/}
                            <IonRow className="campusFacilTableRow">

                            </IonRow>
                        </IonGrid>
                    </IonSlide>

                    <IonSlide>
                        <h1>Block B</h1>
                    </IonSlide>

                    <IonSlide>
                        <h1>Block C</h1>
                    </IonSlide>

                    <IonSlide>
                        <h1>Block D</h1>
                    </IonSlide>
                </IonSlides>
            </IonContent>
        </IonPage>
    );
};

export default CampusFacilitiesMap;
