                                            <IonRouterLink className="progCourseTitleLink" href="/u/study@SIMMain/business/study@SIMProgInfo">
                                                <IonTitle className="progCourseTitle">
                                                    <div className="ion-text-wrap">MSc Management in International Business</div>
                                                </IonTitle>
                                            </IonRouterLink>
                                        </IonRow>

