import { IonButton, IonCol, IonContent, IonGrid, IonImg, IonPage, IonRouterLink, IonRow, IonSegment, IonSegmentButton, IonText, IonTitle, IonToolbar } from '@ionic/react';
import React, { useState } from 'react';

import { auth } from '../firebase';

import '../css/Global.css';
import '../css/Home.css';
import gif from '../img/home/SIM Open House 2020 Banner.gif';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar, faCalendarAlt, faComments, faMapSigns, faMapMarkedAlt, faInfoCircle,
  faPhotoVideo, faBookOpen, faHands, faBell } from '@fortawesome/free-solid-svg-icons';
import { faReadme } from '@fortawesome/free-brands-svg-icons';

import TopNav from '../components/TopNav'

const ProgrammeTalks: React.FC = () => {
    const [dayNum, setDayNum] = useState('day1');

    const handleClick = () => {
        setDayNum('day1');
    }

    const handleClick2 = () => {
        setDayNum('day2');
    }

    return(
        <IonPage>
            <TopNav title="Programme Talks" route="/u/openHouseMain" backarrow={ true } hamburger={ true } />
            
            <IonContent fullscreen>
                <IonGrid>
                    <IonRow>
                        <IonCol>
                            <IonToolbar>
                                <IonSegment value="day1" id="guidedTours-mainHeader">
                                    <IonSegmentButton value="day1" className="guidedTours-heading" >Day 1: 21 Nov 2020</IonSegmentButton>
                                    <IonSegmentButton value="day2" className="guidedTours-heading" onClick={ () =>  handleClick2()}>Day 2: 22 Nov 2020</IonSegmentButton>
                                </IonSegment>
                            </IonToolbar>
                        </IonCol>
                    </IonRow>

                    
                </IonGrid>

            </IonContent>

        </IonPage>

    );
};

export default ProgrammeTalks;
