import { IonCol, IonContent, IonGrid, IonHeader, IonItem, IonLabel, IonPage, IonRow, IonTitle, IonToggle } from '@ionic/react';
import React, { useState } from 'react';

import '../css/Global.css';
import '../css/Settings.css';

import TopNav from '../components/TopNav';
import Menu from '../components/Menu';


const Settings: React.FC = () => {
    const [announcementAlertChecked, setAnnouncementAlertChecked] = useState(false);


    return (
        <IonPage>
            <IonHeader>
                <TopNav title="Settings" route='/u/home' backarrow={ true } hamburger = { true }/>
            </IonHeader>

            <IonContent fullscreen={true}>
                <IonGrid id="settingsGrid">
                    <IonRow className="settingsRow">
                        <IonCol className="settingsColDetails">
                            <IonRow>
                                <IonItem className="settingsItem" lines="none">
                                    <IonTitle className="settingsTitle">Announcement Alerts</IonTitle>
                                    <IonToggle slot="end" name="blueberry" checked={announcementAlertChecked}></IonToggle>
                                </IonItem>
                            </IonRow>

                            <IonRow>
                                <IonLabel className="settingsDetails">Feeling abit snacky after the talks? Come on down to Block B to redeem free popcorns and candy floss! While stock lasts!</IonLabel>
                            </IonRow>

                        </IonCol>
                    </IonRow>

                </IonGrid>
            </IonContent>

        </IonPage>
    );
};

export default Settings;
