import { IonButton, IonCheckbox, IonCol, IonFooter, IonGrid, IonIcon, IonItem, IonItemOption, IonItemOptions, IonItemSliding, IonLabel, IonRow, IonText, IonTitle, IonToolbar } from '@ionic/react';
import React, { useRef, useState } from 'react';

import '../../css/Global.css';
import '../../css/CourseFilterPopoverContent.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { chevronDownOutline, chevronUpOutline } from 'ionicons/icons';

const CourseFilterPopoverContent: React.FC<{
    removeProg: any;
    removeAllProg: any;
    viewResults: any;
    params: any;
    href: any;
}> = props => {

    const info = useRef<HTMLIonRowElement>(null);
    const showIcon = useRef<HTMLIonIconElement>(null);

    const displayInfoCol = () => {
        info.current!.hidden = !info.current!.hidden;
        if (showIcon.current!.icon == chevronDownOutline)
            showIcon.current!.icon = chevronUpOutline;
        else
            showIcon.current!.icon = chevronDownOutline;
    };

    {/* Check if filter is checked */}
    const [checked, setChecked] = useState(false)

    return (
        <>
            <IonGrid id="courseFilterPopoverGrid">
                <IonRow className="filterCourseHeaderRow">
                    <IonCol className="filterCourseHeaderCol">

                        <IonRow className="filterCourseInnerRow" class="ion-align-items-center">
                            <IonCol size="10" sizeSm="10" style={{ padding: "0" }} class="ion-text-left">
                                <IonTitle className="filterCourseHeader">
                                    <div className="ion-text-wrap">
                                        Mode of Study
                                    </div>
                                </IonTitle>
                            </IonCol>

                            <IonCol size="2" sizeSm="2" style={{ padding: "0 2% 0 0" }} className="toggleFilterCourseBtnCol">
                                <IonButton className="toggleFilterCourseBtn" onClick={displayInfoCol} fill="clear" size="default">
                                    <IonIcon slot="icon-only" ref={showIcon} icon={chevronDownOutline} />
                                </IonButton>
                            </IonCol>
                        </IonRow>

                        <IonRow class="ion-align-items-center">
                            <IonCol sizeSm="12" className="courseFilters" ref={info} hidden={true}>
                                <IonCheckbox slot="start" checked={checked} onIonChange={e => setChecked(e.detail.checked)} />
                                <IonLabel id="courseFilterMOSLabel">
                                    <div className="ion-text-wrap">Full</div>
                                </IonLabel>
                                    
                                
                            </IonCol>
                        </IonRow>
                    </IonCol>
                </IonRow>  

            </IonGrid>
        </>
    );
};

export default CourseFilterPopoverContent;
