import { IonButton, IonButtons, IonCol, IonContent, IonGrid, IonHeader, IonImg, IonItem, IonLabel, IonList, IonMenu, IonMenuToggle, IonPage, IonRouterLink, IonRow, IonSegment, IonSegmentButton, IonText, IonTitle, IonToolbar } from '@ionic/react';
import React, { useEffect, useRef, useState } from 'react';
import { withRouter } from 'react-router-dom';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilter } from '@fortawesome/free-solid-svg-icons';

import '../css/Global.css';
import '../css/ProgrammeTalks.css';


const FilterMenu: React.FC = () => {

    const [toggleFilterMenu, setToggleFilterMenu] = useState(false);
    
    const displayFilterMenu = () => {
        if (toggleFilterMenu == false) {
            console.log("Filter menu is closed");
        }
    };

    return(
        <>
            <React.Fragment>
                <IonButtons slot="end" id="filterIcon">
                    <IonMenuToggle>
                        <IonButton auto-hide="false" onClick={displayFilterMenu}>
                            <FontAwesomeIcon size="lg" icon={faFilter} />                                    
                        </IonButton>
                    </IonMenuToggle>
                </IonButtons>
            </React.Fragment>

            <IonMenu swipeGesture={false} side="end" contentId="filterMenuContent" menuId="filterMenu" id="filterMenuID">            
                <IonContent id="filterMenuContent" className="filterMenuContent" scrollEvents={true} scrollY={true}>
                    <IonList className="menuList">
                        <IonItem className="menuItem clickable ion-activatable" lines="none" routerLink="/u/home">                     
                            {/* <FontAwesomeIcon className="menuIcon" size="lg" icon={faHome} /> */}
                            <IonLabel className="menuLabel">Home</IonLabel>
                        </IonItem>
                    </IonList>
                </IonContent>

            </IonMenu>
        </>
    );
};

export default FilterMenu;
