import { IonButton, IonCol, IonContent, IonGrid, IonHeader, IonImg, IonMenu, IonPage, IonRouterLink, 
  IonRow, IonSplitPane, IonText, IonTitle, IonToolbar, IonList, IonLabel, IonItem, IonItemDivider, IonMenuButton } from '@ionic/react';
import React from 'react';

import { auth } from '../firebase';

import '../css/Global.css';
import '../css/Home.css';
import gif from '../img/home/SIM Open House 2020 Banner.gif';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faCalendar, faCalendarAlt, faComments, faMapSigns, faMapMarkedAlt, faInfoCircle,
  faPhotoVideo, faBookOpen, faHands, faBell, faUserCircle, faCog, faQrcode, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import { faReadme } from '@fortawesome/free-brands-svg-icons';

import TopNavMenu from '../components/TopNavMenu';
import Menu from '../components/Menu';
import TopNavBAMenu from '../components/TopNavBAMenu'

const Home: React.FC = () => {
  const handleLogout = async () => {
    await auth.signOut();
    sessionStorage.clear();
  };

  return (

    <IonContent fullscreen>
                <IonSplitPane contentId="main">
                    {/*--  the side menu  --*/}
                    <IonMenu contentId="main" side="end" menuId="menuID">
                        <IonContent id="menuContent" className="menuContent" scrollEvents={true} scrollY={true}>
                                <IonList className="menuList">
                                    <IonItem className="menuItem clickable ion-activatable" lines="none" routerLink="/u/home">                     
