import { IonAlert, IonButton, IonButtons, IonCol, IonContent, IonGrid, IonHeader, IonInput, IonItem, IonModal, IonPage, IonRouterLink, IonRow, IonSelect, IonSelectOption, IonTitle } from '@ionic/react';
import React, { useRef, useState } from 'react';
import { useForm } from 'react-hook-form';

import '../css/Global.css';
import '../css/MyProfile.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAddressCard, faEnvelope, faMobileAlt, faBirthdayCake, faUserGraduate, faGlobe, faLock } from '@fortawesome/free-solid-svg-icons';
import { faEdit } from '@fortawesome/free-regular-svg-icons';

import TopNav from '../components/TopNav';
import Menu from '../components/Menu';


const MyProfile: React.FC = () => {

    const { register, handleSubmit, errors, getValues, reset } = useForm();

    const [firstName, setFirstName] = useState<string>();
    const [lastName, setLastName] = useState<string>();
    const [email, setEmail] = useState<string>();
    const [contactNo, setContactNo] = useState<string>(); 
    const [dob, setDob] = useState();
    const [highestQualification, setHighestQualification] = useState();
    const [nationality, setNationality] = useState();
    const [password, setPassword] = useState();


    {/* Edit Password Fields Alert */}
    // const [editPasswordAlert, setEditPasswordAlert] = useState(false);
    const [changePasswordSuccess, setChangePasswordSuccess] = useState(false);

    const [editPasswordModal, setEditPasswordModal] = useState(false);

    const displayEditPassword = () => {
        // setEditPasswordAlert(true);
        setEditPasswordModal(true);
    };

    const onSubmit = () => {
        console.log("Form Submitted");
    };
    
    return(
        <React.Fragment>
                <IonModal isOpen={editPasswordModal} cssClass='editPasswordModal'>
                    <IonContent fullscreen>
                        <IonGrid id="editPasswordModalGrid">
                            <form onSubmit={handleSubmit(onSubmit)}>
                                {/* Current Password */}
                                <IonRow className="editPasswordModalRow" class="ion-align-items-center">
                                    <IonCol size="12" sizeSm="12" className="editPasswordModalCol" class="ion-text-left">
                                        <IonInput type="password" id="currentPassword" name="currentPassword" placeholder="Current Password" ref={register({ required: true, minLength: 8, pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[.,/<>#^~`@$!%*?&])[A-Za-z\d.,/<>#~`^@$!%*?&]{8,}$/ })} />
                                        {errors.currentPassword && errors.currentPassword.type === "required" && <div className="errorMsg">Current Password is required!</div>}
                                        {errors.currentPassword && errors.currentPassword.type === "minLength" && <div className="errorMsg">Password has to be at least 8 characters and must contain 1 uppercase, 1 lowercase, 1 number and 1 special character</div>}
                                        {errors.currentPassword && errors.currentPassword.type === "pattern" && <div className="errorMsg">Password has to be at least 8 characters and must contain 1 uppercase, 1 lowercase, 1 number and 1 special character</div>}
                                    </IonCol>
                                </IonRow>

                                <IonRow class="ion-align-items-center">
                                    <IonCol size="6" sizeSm="6" class="ion-text-center">
